# Prevent Octave from thinking that this# is a function file:1;A = load("simgraph.txt")function [size volume] = compute_statistics(A)size = (size(A))(1,1);volume = sum(sum(A))/2;endfunction[size volume] = compute_statistics(A)function [A] = load_sparse (filename)B = load(filename);A = sparse(B);endfunction[sparseA] = load_sparse("simgraph-sparse.txt")function [A] = load_sparse_as_identical(filename)C = load(filename);i = C'(1,:);j = C'(2,:);sv(1:length(i)) = 1;dimension = max(max(i),max(j));A = sparse(i,j,sv,dimension,dimension);endfunctionB = load_sparse_as_identical("simgraph-sparse.txt")function [degrees] = compute_degrees(A)s_a = sparse(A)degrees = full(sum(s_a));endfunctiondegrees = compute_degrees(A)function [avgdegree] = compute_avgdegree(A)degrees = compute_degrees(A);size = size(A);avgdegree = sum(degrees)/size(1);endfunctionavgdegree = compute_avgdegree(A)