# Prevent Octave from thinking that this# is a function file:1;clear;A = load("simgraph.txt")sizeA = size(A)function [size volume] = compute_statistics(A)size = (size(A))(1,1);volume = sum(sum(A))/2;endfunction[size volume] = compute_statistics(A)function [A] = load_sparse (filename)B = load(filename);A = full(sparse(B));endfunctionsparseA = load_sparse("simgraph-sparse.txt")function [A] = load_sparse_as_identical(filename)C = load(filename);i = C'(1,:);j = C'(2,:);sv(1:length(i)) = 1;dimension = max(max(i),max(j));A = full(sparse(i,j,sv,dimension,dimension));A = A + A';endfunctionB = load_sparse_as_identical("simgraph-sparse.txt")nnz = nnz(B)[sizeSprs volumeSprs] = compute_statistics(B)function [degrees] = compute_degrees(A)degrees = sum(A');endfunctiondegrees = compute_degrees(B)function [avgdegree] = compute_avgdegree(A)degrees = compute_degrees(A);size = size(A);avgdegree = sum(degrees)/size(1);endfunctionavgdegree = compute_avgdegree(B)